package com.ruby.java.ch02;

public class Test27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 10; i < 10; i++) {
			System.out.println("for ok");
		}
		int j = 10;
		while (j < 10) {
			System.out.println("while ok");
			j++;
		}
		int k = 10;
		do {
			System.out.println("do-while ok");
			k++;

		} while (k < 10);
	}

}
